import {
    CREATE_PRODUCT,
    RETRIEVE_PRODUCTS,
    UPDATE_PRODUCT
  } from "./type";

  import ProductDataService from "../services/product.service";

  const Product = function(product) {
    this.title = product.title;
    this.price = product.price;
    this.description = product.description;
    this.category = product.category;
    this.image = product.image;
    this.quantity = product.quantity;
  };

  export const createProduct = (title, price, description, category, image, quantity) => async (dispatch) => {
    try {
      const res = await ProductDataService.create({ title, price, description, category, image, quantity });

      dispatch({
        type: CREATE_PRODUCT,
        payload: res.data,
      });

      return Promise.resolve(res.data);
    } catch (err) {
      return Promise.reject(err);
    }
  };


  export const retrieveProducts = () => async (dispatch) => {
    try {
      const res = await ProductDataService.getAll();

      dispatch({
        type: RETRIEVE_PRODUCTS,
        payload: res.data,
      });
    } catch (err) {
      console.log(err);
    }
  };


  export const updateProduct = (id, data) => async (dispatch) => {
    try {
      const res = await ProductDataService.update(id, data);

      dispatch({
        type: UPDATE_PRODUCT,
        payload: data,
      });

      return Promise.resolve(res.data);
    } catch (err) {
      return Promise.reject(err);
    }
  };
